// contracts/LoyaltyProgram.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "./LoyaltyToken.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract LoyaltyProgram is Ownable, ReentrancyGuard {
    LoyaltyToken public loyaltyToken;
    
    struct Customer {
        bool isRegistered;
        uint256 totalEarned;
        uint256 totalRedeemed;
        uint256 lastActivity;
    }
    
    struct Reward {
        string name;
        string description;
        uint256 cost;
        bool isActive;
        uint256 stock;
    }
    
    mapping(address => Customer) public customers;
    mapping(uint256 => Reward) public rewards;
    mapping(address => mapping(uint256 => bool)) public customerRedemptions;
    
    uint256 public nextRewardId = 1;
    uint256 public pointsPerPurchase = 10; // Points earned per dollar spent
    
    event CustomerRegistered(address indexed customer);
    event PointsEarned(address indexed customer, uint256 amount, string reason);
    event PointsRedeemed(address indexed customer, uint256 rewardId, uint256 cost);
    event RewardAdded(uint256 indexed rewardId, string name, uint256 cost);
    
    constructor(address _tokenAddress) Ownable(msg.sender) {
        loyaltyToken = LoyaltyToken(_tokenAddress);
    }
    
    function registerCustomer() public {
        require(!customers[msg.sender].isRegistered, "Customer already registered");
        
        customers[msg.sender] = Customer({
            isRegistered: true,
            totalEarned: 0,
            totalRedeemed: 0,
            lastActivity: block.timestamp
        });
        
        // Welcome bonus
        loyaltyToken.mint(msg.sender, 50 * 10**18);
        customers[msg.sender].totalEarned += 50 * 10**18;
        
        emit CustomerRegistered(msg.sender);
        emit PointsEarned(msg.sender, 50 * 10**18, "Welcome bonus");
    }
    
    function earnPoints(address customer, uint256 purchaseAmount, string memory reason) 
        public onlyOwner {
        require(customers[customer].isRegistered, "Customer not registered");
        
        uint256 points = purchaseAmount * pointsPerPurchase * 10**18;
        loyaltyToken.mint(customer, points);
        
        customers[customer].totalEarned += points;
        customers[customer].lastActivity = block.timestamp;
        
        emit PointsEarned(customer, points, reason);
    }
    
    function addReward(
        string memory name,
        string memory description,
        uint256 cost,
        uint256 stock
    ) public onlyOwner {
        rewards[nextRewardId] = Reward({
            name: name,
            description: description,
            cost: cost * 10**18,
            isActive: true,
            stock: stock
        });
        
        emit RewardAdded(nextRewardId, name, cost * 10**18);
        nextRewardId++;
    }
    
    function redeemReward(uint256 rewardId) public nonReentrant {
        require(customers[msg.sender].isRegistered, "Customer not registered");
        require(rewards[rewardId].isActive, "Reward not active");
        require(rewards[rewardId].stock > 0, "Reward out of stock");
        require(!customerRedemptions[msg.sender][rewardId], "Reward already redeemed");
        
        uint256 cost = rewards[rewardId].cost;
        require(loyaltyToken.balanceOf(msg.sender) >= cost, "Insufficient points");
        
        loyaltyToken.burn(msg.sender, cost);
        customers[msg.sender].totalRedeemed += cost;
        customers[msg.sender].lastActivity = block.timestamp;
        rewards[rewardId].stock--;
        customerRedemptions[msg.sender][rewardId] = true;
        
        emit PointsRedeemed(msg.sender, rewardId, cost);
    }
    
    function getCustomerBalance(address customer) public view returns (uint256) {
        return loyaltyToken.balanceOf(customer);
    }
    
    function setPointsPerPurchase(uint256 _pointsPerPurchase) public onlyOwner {
        pointsPerPurchase = _pointsPerPurchase;
    }
    
    function toggleRewardStatus(uint256 rewardId) public onlyOwner {
        rewards[rewardId].isActive = !rewards[rewardId].isActive;
    }
}
